package ex11_WhileLoop;

public class Lab01_WhileSchema {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i =0; // initialization
		while(i<5) // condition
		{
			System.out.println(i);
			i++; // Updation
		}
		
		// I C U
		 for (int j = 0; j < 5 ; j++) {
	            System.out.println(j);
	        }

	}

}
